from django.contrib.auth.signals import user_logged_out  # noqa
from django.dispatch import Signal

providing_args = f""

user_logged_in = Signal(["request", "user"])

# Typically followed by `user_logged_in` (unless, e-mail verification kicks in)
user_signed_up = Signal(["request", "user"])

password_set = Signal(["request", "user"])
password_changed = Signal(["request", "user"])
password_reset = Signal(["request", "user"])

email_confirmed = Signal(["request", "email_address"])
email_confirmation_sent = Signal(["request", "confirmation", "signup"])

email_changed = Signal(["request", "user", "from_email_address", "to_email_address"])
email_added = Signal(["request", "user", "email_address"])
email_removed = Signal(["request", "user", "email_address"])
